const ShopScreens = observer(({navigation}) => {
  const [coastProduct, setCoastProduct] = React.useState();
  const [userBalance, setUserBalance] = React.useState();
  const [showAlert, setShowAlert] = React.useState(false);
  const [freezeItem, setFreezeItem] = React.useState(null);

  const showAlertfunc = () => {
    setShowAlert(true)
  };

  const hideAlertfunc = () => {
    setShowAlert(false)
  };

  const getUserInfo = async () => {
    const userResponse = await fetch(
      'https://www.ecomap.online/api/auth/user/',
      {
        method: 'GET',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          Authorization: `Token ${UserStore.token}`,
        },
      },
    );
    const user = await userResponse.json();
    console.log(user)
    UserStore.setMoney(user.balance);
    setUserBalance(user.balance)
    setUserFreezCount(user.freeze_item_count)
  };

  const getCoast = async () => {
    const coastResponse = await fetch(
      'https://www.ecomap.online/api/catalog/product',
      {
        method: 'GET',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          Authorization: `Token ${UserStore.token}`,
        },
      },
    );
    const coast = await coastResponse.json();

    setCoastProduct(coast.price);
  };

  const buyFreez = async () => {
    if(userBalance < 100){
      setShowAlert(true)
    } else {
      setUserBalance(userBalance - 100)
    }
    const response = await fetch(
      'https://www.ecomap.online/api/catalog/product/',
      {
        method: 'POST',
        headers: {
          'Content-Type':
            'multipart/form-data; boundary=—-WebKitFormBoundaryfgtsKTYLsT7PNUVD',
          Authorization: `Token ${UserStore.token}`,
        },
      },
    );
    const result = await response.json();
    
    console.log(result);
  };

  const showBuyWarning = () => {
    alert('Вы не можете приобрести товар');
  };

  React.useEffect(() => {
    const unsubscribe = navigation.addListener('focus', async () => {
      getCoast();
      getUserInfo();
      setFreezeItem(ShopStore.items['freeze']);
    });
    return unsubscribe;
  }, [navigation]);

  return (
    <View style={styles.container}>
      <View style={styles.ShopTitle}>
        <Text style={styles.ShopTitleText}>Мой счет</Text>
        <View style={styles.ShopTitleCoin}>
          {/* <UserPropDisplay style={styles.TextCoin} name={'money'} /> */}
          <Text style={styles.TextCoin}>{userBalance}</Text>
          <Image source={require('../assets/coin.png')} />
        </View>
      </View>
      <View style={styles.ShopItems}>
        <AwesomeAlert
          show={showAlert}
          showProgress={false}
          title="Недостаточно денег"
          message="Пополните баланс чтобы купить предмет"
          closeOnTouchOutside={true}
          closeOnHardwareBackPress={false}
          showConfirmButton={true}
          confirmText="Закрыть"
          confirmButtonColor="#1E9D2D"
          onCancelPressed={() => {
            hideAlertfunc();
          }}
          onConfirmPressed={() => {
            hideAlertfunc();
          }}
        />
        <View style={styles.ShopItem}>
          <View style={styles.ShopItemTitle}>
          <SvgXml style={styles.ShopItemImage} xml={shopItemSvg} />
            <Text style={styles.ShopItemTitleText}>Ледниковый период</Text>
          </View>
          <View style={styles.ShopItemSubTitle}>
            <Text style={styles.ShopItemSubTitleText}>
              {coastProduct} монет
            </Text>
            <SvgXml style={styles.ShopItemImage} xml={coinMiniSvg} />
          </View>
          <Text style={styles.ShopItemDescr}>
            Купив эту способность, любая выбранная вами улица заморозится за
            вами на 14 дней. Никто не сможет ее занять в течение этого времени.
          </Text>
          {freezeItem ? (
            !freezeItem.isActive ? (
              <View style={{ width: '100%', marginBottom: 20, marginTop: 20 }}>
                <AwesomeButton 
                  type="primary"
                  backgroundColor="#1E9D2D"
                  backgroundDarker="#3D7606"
                  borderRadius={15}
                  width="100%"
                  onPress={() => buyFreez()}
                >
                  <Text style={styles.textStyle}>Купить</Text>
                </AwesomeButton>
              </View>
            ) : (
              <View style={{ width: '100%', marginBottom: 20, marginTop: 20 }}>
                <AwesomeButton 
                  type="primary"
                  backgroundColor="#1E9D2D"
                  backgroundDarker="#3D7606"
                  borderRadius={15}
                  width="100%"
                  onPress={() => showBuyWarning()}
                >
                  <Text style={styles.textStyle}>{freezeItem.time}</Text>
                </AwesomeButton>
              </View>
            )
          ) : null}
        </View>
      </View>
    </View>
    // <View style={styles.conteinerBuild}>
    //   <Text style={styles.textBuild}>Страница в разработке</Text>
    // </View>
  );
});

